/**
 * Created by oashery on 3/2/2016.
 */

if (Meteor.isClient){
  Accounts.onLogin(function () {
    Router.go('/home');
  });

  Accounts.onLogout(function () {
    Router.go('/');
  });
}

Router.configure({
    layoutTemplate: 'main',
    loadingTemplate: 'loading'
});

Router.route('/', {
    name: 'homePage',
    template: 'mainPage'
});

Router.route('home', {
    path: '/home',
    waitOn: function () {
        return Meteor.subscribe('inventory');
    },
    action: function () {
        if (this.ready()){

            this.state.set('envName', this.params.query.env);
            /*
                        if(query){
                                //return Inventory.find({$where: "this.id_path.match('^/WebEX-Mirantis@Cisco/')"});
                                console.log(query);
                                this.render('home', {
                                    data: function () {
                                        return Inventory.find({environment: query, parent_id: query});
                                    }
                                });
                                //
                        }
            */

        // if the sub handle returned from waitOn ready() method returns
        // true then we're ready to go ahead and render the page.
            this.render('home');

        }
        else{
            // otherwise render the loading template.
            this.render('loading');
        }
    }
});

Router.route('landingpage', {
    name: 'landingpage',
    path: '/landing'
});

Router.route('d3plusgraph', {
    path: '/d3plus'
});

Router.route('threeTest', {
    path: '/three',
});

Router.route('threeTest2', {
    path: '/three2',
});

Router.route('wizard', {
    name: 'wizard',
    path: '/wizard'
});

Router.route('dashboard', {
    name: 'dashboard',
    path: '/dashboard'
});
